{
    Stack st1;
    st1.push (11.1);
    st1.push (22.2);
    st1.push (33.3);
    cout &lt;&lt; st1.pop () &lt;&lt; endl; // 弹出 33.3，栈中只剩两个数据
    st1 [1] = 0;
    cout &lt;&lt; st1 [0] &lt;&lt; "," &lt;&lt; st1 [1] &lt;&lt; endl; // 输出 11.1，0
